import React, {useState, useEffect} from 'react'

export const Garchomp = () => {
    const [garchomp, setGarchomp] = useState();
    useEffect(() => {
      Data();
      Refresh();
    },[]);

    const Data = async () => {
      const data = await fetch("https://pokeapi.co/api/v2/pokemon/garchomp")
      const gible = await data.json();
      setGarchomp(gible);
  }

    const Refresh = () => {
      document.getElementById("garchomp").src = garchomp.sprites.front_default;
    }

  return (
    <div>
      <img id='garchomp'></img>
      
      <h4>Escriba el nombre del pokemon que desea buscar en minusculas</h4>
    </div>

  )
}
