import React, {useState, useEffect} from 'react'

export const Input = () => {
    const [pikachu, setPikachu] = useState();
    const [loaded, setLoaded] = useState(false);
    useEffect(() => {
        fetch("https://pokeapi.co/api/v2/pokemon/pikachu")
        .then(res => res.json())
        .then((pokemon) => setPikachu(pokemon))
        .catch(err => console.log(err))
        .finally(() => {setLoaded(true)});
    },[]);
  return (
    <div>
        <input placeholder='Nombre del pokemon'></input>
        {/* <input value={"Buscar"} aria-label='Buscar' name='btnk' role={"button"} type='submit'></input> */}           

        
        <button onClick={() => {
            console.log(pikachu);
            document.getElementById("Name").textContent = pikachu.name;
            document.getElementById("Experience").textContent = pikachu.base_experience;
            document.getElementById("Sprite").src = pikachu.sprites.front_default;
            document.getElementById("Hp").textContent = pikachu.stats[0].base_stat;
        }}>Buscar</button>

        <br></br>
        <div>
        <h2>Nombre: </h2>
        <h2 id='Name'></h2>
        </div>
        <h2 id='Experience'></h2>
        <h2 id='Hp'></h2>
        <img id='Sprite'></img>


        <img src={pikachu.sprites.front_default}></img>
        </div>
  )
}
